import React, { useEffect, useState } from 'react'
import { ArrowCircleUp } from '@mui/icons-material';

export default function GotoTop() {
    const [isVisible, setIsVisible] = useState(false);
    const gotoBtn = () => {
        window.scrollTo({top:0,left:0, behavior:"smooth"} );
    }
    const listenTOScroll = () => {
        let hight= 650;
        const winScroll = document.body.scrollTop || document.documentElement.scrollTop;

        if(winScroll >hight){
setIsVisible(true);
        }
        else{
            setIsVisible(false);
        }
    }
    useEffect(() => {
        window.addEventListener("scroll",listenTOScroll);
        return () => window.removeEventListener("scroll", listenTOScroll);
    })
  return (
    <>
    {isVisible && (
        <div className=' flex justify-center  rounded-full fixed bottom-32 right-14 z-50' onClick={gotoBtn}>
       <ArrowCircleUp style={{ fontSize:"40px"}} className=' bg-purple-800 text-white drop-shadow-lg shadow-purple-800 rounded-full animate-bounce' />
    </div>
    )}
    </>

  )
}
