import React, { useEffect, useRef, useState } from "react";
import JobsComponent from "./JobsComponent";
import data from "../../static/data.json";
import image from "../../static/Images/My project.png";
import { Search } from "@mui/icons-material";
import image1 from "../../static/Images/1681094203055.jpg";
import Footer from "../NavFooter/Footer";
import Navbar from "../NavFooter/Navbar"
import GotoTop from "../GotoTop";
import Typewriter from "typewriter-effect";
import PostJob from "./PostJob";
import JobApply from "./JobApply";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [search, setSearch] = useState("");
  const inputRef = useRef(null);
  const intRef = useRef(null);

  useEffect(() => setJobs(data), []);

  const handleClick = () => {
    setSearch(intRef.current.value, inputRef.current.value);
  };

  return (
    <>
    <Navbar />
      <div className="flex justify-start flex-col pb-20 pt-16">
        <div
          className=" w-full bg-cover text-white"
          style={{ backgroundImage: `url(${image1})` }}
        >
          <div className=" flex md:p-10 p-2 text-white">
            <div className=" flex flex-col md:p-10 p-2">
              <div className=" flex flex-col justify-start  w-4/6">
                <span className=" text-4xl font-bold md:text-5xl">
                  <Typewriter
                    options={{
                      strings: "Get your",
                      autoStart: true,
                      loop: true,
                      delay: 120,
                    }}
                  />
                  <Typewriter
                    options={{
                      strings: "dream Job",
                      autoStart: true,
                      loop: true,
                      delay: 102,
                    }}
                  />
                  <Typewriter
                    options={{
                      strings: "Today",
                      autoStart: true,
                      loop: true,
                      delay: 210,
                    }}
                  />
                </span>
                <p className=" py-10">
                  Explore all the most exciting job roles based on youre
                  interest and study major
                </p>
              </div>
              <div className=" flex flex-col md:flex-row bg-indigo-700 flex-wrap justify-center items-center text-white rounded-md p-2">
                <div className=" flex justify-start flex-wrap pl-2 ">
                  <div className="  text-white ">
                    <h2>Location</h2>
                    <input
                      type="text"
                      ref={intRef}
                      placeholder="Enter Location"
                      className=" placeholder-black placeholder-opacity-70 text-gray-800 focus:outline-none bg-transparent"
                    />
                  </div>
                  <div className="border-b-white border-b-2 w-full py-1 md:border-none md:py-0"></div>
                  <div className=" pt-1 ">
                    <h2>Type</h2>
                    <input
                      type="text"
                      ref={inputRef}
                      placeholder="Enter Type of Job"
                      className=" placeholder-black placeholder-opacity-70  text-gray-800 focus:outline-none bg-transparent "
                    />
                  </div>
                </div>
                <div className=" ">
                  <button
                    className="  bg-orange-400 text-white p-1 rounded-md hover:bg-orange-300"
                    onClick={handleClick}
                  >
                    <Search /> Search
                  </button>
                </div>
              </div>
            </div>

            <img
              src={image}
              className=" md:pl-20 md:w-5/12 md:rounded-3xl w-0"alt=""
            />
          </div>
        </div>
        <div className="pl-12 pt-12 bg-purple-50 pb-12">
          <PostJob />
        </div>
        <div className=" flex pl-12 pt-12 ">
          <h1 className=" text-3xl border-b border-b-teal-400 font-serif  text-teal-500">
            JOBS
          </h1>
        </div>
        <div >
          {jobs.length === 0 ? (
            <p>fetching</p>
          ) : (
            jobs
              .filter((job) => {
                return search.toLowerCase() === ""
                  ? job
                  : job.location.toLowerCase().includes(search) ||
                      job.role.toLowerCase().includes(search);
              })
              .map((job) => <JobsComponent job={job} key={job.id} />)
          )}
        </div>
      </div>
      <GotoTop />
      <Footer />
    </>
  );
};

export default Home;
