import React, { useState } from "react";
import loginbg from '../../static/Images/login_background.gif'
import EmailIcon from '@mui/icons-material/Email';
import LockIcon from '@mui/icons-material/Lock';
import { Link, useNavigate } from "react-router-dom";
import { auth } from "../../firestore";
import imglogin from '../../static/Images/LOGO_KRITANJ.png'
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";

export default function Signup() {

  const navigate = useNavigate();
  const [values, setValues] = useState({
    name: "",
    email: "",
    pass: "",
  });
  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    if (!values.name || !values.email || !values.pass) {
      setErrorMsg("Fill all fields");
      return;
    }
    setErrorMsg("");

    setSubmitButtonDisabled(true);
    createUserWithEmailAndPassword(auth, values.email, values.pass)
      .then(async (res) => {
        setSubmitButtonDisabled(false);
        const user = res.user;
        await updateProfile(user, {
          displayName: values.name,
        });
        navigate("/Register");
      })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message);
      });
  };

  return (
    <div className="flex flex-wrap flex-col-reverse md:flex-row items-center justify-center h-screen w-screen py-8 bg-gradient-to-t from-emerald-300 to-indigo-300 fixed top-0 left-0 right-0">
      <div className=" w-64 h-48 md:w-5/12 md:h-4/6 bg-cover text-white rounded-xl" style={{backgroundImage:`url(${loginbg})`}}>
      </div>
      <div className=" flex flex-col justify-center items-center flex-wrap bg-transparent rounded-lg  mb-10 p-4 md:ml-20 ">
        <img src={imglogin} className=" pl-5" alt="" />
        <div className="  pb-2 border-solid border-b-2 w-full justify-center text-center mb-2">
          <h1 className=" font-serif text-3xl underline text-blue-900">Sign Up</h1>
        </div>
        <form class="" action="post">
        <input
          label="Name"
          placeholder="Enter your name"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, name: event.target.value }))
          }
        />
          <div className=" pb-2">
            <label
              for="email"
              class="block mb-2 text-sm font-medium text-gray-900"

            >
              Email
            </label>
            <label className="relative text-gray-600 justify-center w-full items-center bg-white p-1 flex rounded-lg">
              <EmailIcon className="pl-1" />
              <input
               onChange={(event) =>
                setValues((prev) => ({ ...prev, email: event.target.value }))
              }
              type="email"
              name="email"
              id="email"
              class=" text-gray-900 sm:text-sm rounded-lg block w-full p-2  focus:outline-none"
              placeholder="Enter your Email..."
              required=""
            />
            </label>
          </div>
          <div className=" w-72">
            <label
              for="password"
              class="block mb-2 text-sm font-medium text-gray-900"
            >
              Password
            </label>
            <label className="relative text-gray-600 justify-center w-full items-center bg-white p-1 flex rounded-lg">
              <LockIcon className="pl-1"/>
              <input
              type="password"
              name="password"
              id="password"
              placeholder="••••••••"
              onChange={(event) =>
                setValues((prev) => ({ ...prev, pass: event.target.value }))
              }
              class="text-gray-900 sm:text-sm rounded-lg block w-full p-2 focus:outline-none"
              required=""
            />
            </label>
          </div>
          <b className="">{errorMsg}</b>
          <button
            type="submit"
            onClick={handleSubmission} disabled={submitButtonDisabled}
            class="w-full text-white bg-blue-600 hover:bg-blue-900 focus:outline-none  font-medium rounded-lg text-sm mt-7 px-5 py-3 text-center "
          >
            Sign up
          </button>
          <p class="text-sm font-light text-gray-500 pt-4">
            Already have an account?
            <Link
              to={"/"}
              class="font-medium text-blue-600 hover:underline pl-1"
            >
              Sign in
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
}

