import React, { useState } from "react";
import EmailIcon from "@mui/icons-material/Email";
import LockIcon from "@mui/icons-material/Lock";
import PersonIcon from "@mui/icons-material/Person";
import CalendarMonthIcon from "@mui/icons-material/CalendarMonth";
import DomainIcon from "@mui/icons-material/Domain";
import DynamicRegister from "./DynamicRegister";
import { GitHub, LinkedIn, Twitter } from "@material-ui/icons";

import { motion } from "framer-motion";
import {
  MdFastfood,
  MdCloudUpload,
  MdDelete,
  MdFoodBank,
  MdAttachMoney,
} from "react-icons/md";
import { firestore } from '../../firestore';
import { addDoc, collection } from '@firebase/firestore'
import { useStateValue } from "../../context/StateProvider";
import { deleteObject, getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'
import { storage } from "../../firestore";
import Loader from "../Loader";
import { saveItem } from '../../utils/firebaseFunctions';
import { getAllDetails } from "../../utils/firebaseFunctions";

import { actionType } from "../../context/reducer";


export default function NewRegister() {
  const [Job, setJob] = useState(false);
  const [pass,setPass] = useState(false);


  const [name, setName] = useState("");
  const [Job_deatils_id, set_Job_Details_id] = useState(null);
  const [passout_year, setPassout_year] = useState("");
  const [branch, setBranch] = useState("");
  const [Job_deatils, set_JobDetails] = useState("");
  const [Job_offer, set_JobOffer] = useState("");
  const [imageAsset, setImageAsset] = useState(null);
  const [fields, setFields] = useState(false);
  const [alertStatus, setAlertStatus] = useState("danger");
  const [msg, setMsg] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [{ details }, dispatch] = useStateValue();



  const uploadImage = (e) => {
    setIsLoading(true);
    const imageFile = e.target.files[0];
    const storageRef = ref(storage, `images/${Date.now()}-${imageFile.name}`);
    const uploadTask = uploadBytesResumable(storageRef, imageFile);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const uploadProgress =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
      },
      (error) => {
        console.log(error);
        setFields(true);
        setMsg("Error while uploading : Try AGain ðŸ™‡");
        setAlertStatus("danger");
        setTimeout(() => {
          setFields(false);
          setIsLoading(false);
        }, 4000);
      },
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          setImageAsset(downloadURL);
          setIsLoading(false);
          setFields(true);
          setMsg("Image uploaded successfully ðŸ˜Š");
          setAlertStatus("success");
          setTimeout(() => {
            setFields(false);
          }, 4000);
        });
      }
    );
  }

  const saveDetails = () => {
    setIsLoading(true);
    try {
      if (!name || !passout_year || !imageAsset || !branch || !Job_deatils) {
        setFields(true);
        setMsg("Required fields can't be empty");
        setAlertStatus("danger");
        setTimeout(() => {
          setFields(false);
          setIsLoading(false);
        }, 4000);
      } else {
        const data = {
          id: `${Date.now()}`,
          name: name,
          Job_deatils_id:`${Array.length()}`,
          imageURL: imageAsset,
          passout_year: passout_year,
          branch: branch,
          Job_offer: Job_offer,
          Job_deatils: Job_deatils,
        };
        saveItem(data);
        setIsLoading(false);
        setFields(true);
        setMsg("Data Uploaded successfully ðŸ˜Š");
        clearData();
        setAlertStatus("success");
        setTimeout(() => {
          setFields(false);
        }, 4000);

      }
    } catch (error) {
      console.log(error);
      setFields(true);
      setMsg("Error while uploading : Try AGain ðŸ™‡");
      setAlertStatus("danger");
      setTimeout(() => {
        setFields(false);
        setIsLoading(false);
      }, 4000);
    }
    fetchData();

  };

  const clearData = () => {
    setName("");
    setImageAsset(null);
    setPassout_year("");
    setBranch("");
    set_JobDetails("");
    set_JobOffer("");
  };

  const deleteImage = () => {
    setIsLoading(true);
    const deleteRef = ref(storage, imageAsset);
    deleteObject(deleteRef).then(() => {
      setImageAsset(null);
      setIsLoading(false);
      setFields(true);
      setMsg("Image deleted successfully ðŸ˜Š");
      setAlertStatus("success");
      setTimeout(() => {
        setFields(false);
      }, 4000);
    });
  };


  const fetchData = async () => {
    await getAllDetails().then((data) => {
      dispatch({
        type: actionType.SET_DETAILS,
        details: data,
      });
    });
  };





  return (
    <>
      <div className="flex flex-wrap flex-col items-center justify-center  py-8 bg-gradient-to-t from-emerald-300 to-indigo-300 pt-24 h-auto w-auto ">
        <div>
          <h1 className=" font-serif text-3xl underline text-blue-900 bg-transparent">
            Register
          </h1>
        </div>




        <div className=" flex justify-center items-center flex-wrap bg-transparent rounded-lg  mb-10 p-4 pl-14 md:ml-20 ">
          <form className="" action="post">
            <div>
              <label
                className="block mb-2 text-sm font-medium text-gray-900"
              >
                Name
              </label>
              <label className="relative text-gray-600 justify-center w-64 lg:w-full items-center bg-white p-1 flex rounded-lg">
                <PersonIcon className="pl-1" />
                <input
                  type="text"
                  value={name}
              onChange={(e) => setName(e.target.value)}
                  className=" text-gray-900 sm:text-sm rounded-lg block w-60 lg:w-full p-1  focus:outline-none"
                  placeholder="Enter your Name..."
                  required
                />
              </label>
            </div>
            <div className=" pb-2">
              <label
                className="block mb-2 text-sm font-medium text-gray-900"
              >
                Email
              </label>
              <label className="relative text-gray-600 justify-center w-64 lg:w-full items-center bg-white p-1 flex rounded-lg">
                <EmailIcon className="pl-1" />
                <input
                  type="email"

                  className=" text-gray-900 sm:text-sm rounded-lg block w-60 lg:w-full p-1  focus:outline-none"
                  placeholder="Enter your Email..."
                  required
                />
              </label>
            </div>

            <div className=" flex flex-col lg:flex-row">
              <div className=" lg:pr-20">
                <label
                  className="block mb-2 text-sm font-medium text-gray-900"
                >
                  Department
                </label>
                <label className="relative text-gray-600 justify-center w-64 lg:w-full items-center bg-white p-1 flex rounded-lg">
                  <DomainIcon className="pl-1" />
                  <input
                    type="department"
                    value={branch}
              onChange={(e) => setBranch(e.target.value)}
                    placeholder="Enter Department..."
                    className="text-gray-900 sm:text-sm rounded-lg block w-60 lg:w-full p-1 focus:outline-none"
                    required
                  />
                </label>
              </div>
              <div>
                <label
                  className="block mb-2 text-sm font-medium text-gray-900"
                >
                  Passout
                </label>

                <div className="flex flex-row p-1">
                <label className="pr-10">
                  <input
                    type="radio"
                    checked={pass}
                    onChange={() => {
                      setPass(true);
                    }}
                  />
                  <h1>Yes</h1>
                </label>
                <label>
                  <input
                    type="radio"
                    checked={!pass}
                    onChange={() => {
                      setPass(false);
                    }}
                  />
                  <h1>No</h1>
                </label>
              </div>

              {
                pass ? (
                  <>
                  <label className=" relative text-gray-600 justify-center w-64  lg:w-full items-center bg-white p-1 flex rounded-lg">
                  <CalendarMonthIcon className="p-1" />
                  <input
                    type="number"
                    value={passout_year}
              onChange={(e) => setPassout_year(e.target.value)}
                    className="text-gray-900 sm:text-sm rounded-lg block w-60 lg:w-full p-1 focus:outline-none"
                    placeholder="Passout Year..."
                    required
                  />
                </label>
                  </>
                ) : (
                  ""
                )
              }

              </div>
            </div>
            <div className=" flex flex-col lg:flex-row p-2 justify-between"> 
              <label className=" flex w-48 px-1 rounded-full bg-white  items-center relative ml-1 mb-1">
                <LinkedIn />
                <input
                  type="url"
                  placeholder="Enter linkdin link"
                  className="text-gray-900 sm:text-sm rounded-lg block pr-5 w-36 p-1 m-1 focus:outline-none"
                />
              </label>
            </div>
            <div>
              <h1 className=" text-gray-900 font-medium text-sm">
                Do you have any job?
              </h1>
              <div className="flex flex-row p-1">
                <label className="pr-10">
                  <input
                    type="radio"
                    checked={Job}
                    onChange={() => {
                      setJob(true);
                    }}
                  />
                  <h1>Yes</h1>
                </label>
                <label>
                  <input
                    type="radio"
                    checked={!Job}
                    onChange={() => {
                      setJob(false);
                    }}
                  />
                  <h1>No</h1>
                </label>
              </div>
              {Job ? (
                <>
                  <div className=" py-3"  >
                    <DynamicRegister />
                  </div>
                </>
              ) : (
                ""
              )}
            </div>


            <div className="group flex justify-center bg-slate-50 my-4 items-center flex-col border-2 border-dotted border-gray-300 w-full h-225 md:h-340 cursor-pointer rounded-lg">
            {isLoading ? (
              <Loader />
            ) : (
              <>
                {!imageAsset ? (
                  <>
                    <label className="w-full h-full flex flex-col items-center justify-center cursor-pointer">
                      <div className="w-full h-full flex flex-col items-center justify-center gap-2">
                        <MdCloudUpload className="text-gray-500 text-3xl hover:text-gray-700" />
                        <p className="text-gray-500 hover:text-gray-700">
                          Click here to upload
                        </p>
                      </div>
                      <input
                        type="file"
                        name="uploadimage"
                        accept="image/*"
                        onChange={uploadImage}
                        className="w-0 h-0"
                      />
                    </label>
                  </>
                ) : (
                  <>
                    <div className="relative h-full">
                      <img
                        src={imageAsset}
                        alt="uploaded image"
                        className="w-full h-full object-cover"
                      />
                      <button
                        type="button"
                        className="absolute bottom-3 right-3 p-3 rounded-full bg-red-500 text-xl cursor-pointer outline-none hover:shadow-md  duration-500 transition-all ease-in-out"
                        onClick={deleteImage}
                      >
                        <MdDelete className="text-white" />
                      </button>
                    </div>
                  </>
                )}
              </>
            )}
          </div>




            <div className=" lg:justify-center lg:items-center lg:flex flex-col">
              <button
                type="submit"
                onClick={saveDetails}
                className=" w-64 lg:w-80 text-white bg-blue-600 hover:bg-blue-900 focus:outline-none justify-center font-medium rounded-lg text-sm px-5 py-3 text-center "
              >
                Save
              </button> 
            </div>


            {fields && (
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className={`w-full p-2 mt-5 rounded-lg text-center text-lg font-semibold ${alertStatus === "danger"
                ? "bg-red-400 text-red-800"
                : "bg-emerald-400 text-emerald-800"
                }`}
            >
              {msg}
            </motion.p>
          )}

          </form>
        </div>
      </div>
    </>
  );
}
