import React, { useState } from 'react'
import AddBoxIcon from '@mui/icons-material/AddBox';
import IndeterminateCheckBoxIcon from '@mui/icons-material/IndeterminateCheckBox';

export default function DynamicRegister() {
    const [inputList,setInputList] = useState([{company:'',year:''}])

    const handleInputChange = (e,index) => {
        const {name,value} = e.target;
        const list = [...inputList];
        list[index][name] = value;
        setInputList(list);
    }

    const handleRemove = (index) => {
        const list = [...inputList];
        list.splice(index,1);
        setInputList(list);
    }

    const handleClick = () => {
        setInputList([...inputList,{company:'',year:''}]);
    }
  return (
    <>
    <div className=' flex flex-row text-gray-900 font-medium text-sm'>
    <h1 className=' pr-10'>Job Details</h1>



    </div>

    {
        inputList.map((x,i) => {
            return (
                <div className=' flex flex-row  items-center flex-wrap '>

        <div className=' flex flex-col pr-5 '>
            <label className='pb-1'>
                Company Name:
            </label>
            <label>
                <input type='text' name='company' value={x.company} placeholder='Enter company name...' onChange={e => handleInputChange(e,i)} className='text-gray-900 sm:text-sm rounded-lg block w-full p-1 focus:outline-none' required />
            </label>
        </div>
        <div className=' flex flex-col pr-10'>
            <label className='pb-1'>
                Year of Experience
            </label>
            <label>
                <input type='number' name='year' value={x.year} placeholder='Enter year of experience...' onChange={e => handleInputChange(e,i)} className=' text-gray-900 sm:text-sm rounded-lg block w-full p-1 focus:outline-none' required />
            </label>
        </div> 
       <div className=' pl-5 '>
       {
            inputList.length!=1 &&
            <IndeterminateCheckBoxIcon onClick={() => handleRemove(i)} />  
        }
        {
        inputList.length-1===i &&
        <AddBoxIcon onClick={handleClick}/>
        }
       </div>

    </div>
            )
        })
    }

    </>
  )
}
