import React from "react";
import PostJob from "./PostJob";
import { useState } from "react";
import Contest from "../Contest/Contest"
import JobApply from "./JobApply";
import { NavLink } from "react-router-dom";

const JobsComponent = ({
  job: {
    company,
    logo,
    isNew,
    featured,
    position,
    role,
    level,
    postedAt,
    contract,
    location,
    languages,
    tools,
  },
}) => {

  const [Post,setPost] = useState(false);

  const tags = [role, level];

  if (tools) {
    tags.push(...tools);
  }
  if (languages) {
    tags.push(...languages);
  }




  return (
    <div
      className={`flex flex-col bg-teal-50 shadow-md mx-10 my-16 p-6 rounded ${
        featured && "border-l-4 border-teal-500 border-solid "
      } sm:flex-row`}
    >
      <div className=" grid grid-cols-[75px_auto] justify-center items-center  ">
        <div>
          <img
            className="w-20 h-20 rounded-full sm:my-0 "
            src={logo}
            alt=""
          />
        </div>
        <div className=" flex flex-col justify-between ml-4">
          <div className="font-bold text-teal-500 content-between">
            <p className="  hidden align-text-bottom sm:inline ">
              {company}
            </p>
            { isNew && (
              <span className=" bg-teal-500 text-white font-bold m-2 px-2 py-1 rounded-full ">
                NEW
              </span>
            )}
            { featured && (
              <span className=" bg-gray-800 text-white font-bold px-2 py-1 rounded-full">
                FEATURED
              </span>
            )}
            <div className=" md:hidden pt-3">{company}</div>
          </div>
          <h2 className="font-bold text-xl my-2">{position}</h2>
          <p className="text-gray-700">
            {postedAt} • {contract} • {location}
          </p>
        </div>
      </div>
      <div className=" flex flex-wrap items-center mt-4 mx-4 p-4 border-t border-gray-500 border-solid sm:ml-auto sm:border-0 sm:pt-0 sm:mt-0 ">
        {tags
          ? tags.map((tag) => (
              <span className=" text-teal-500 bg-teal-100 font-bold mr-4 mb-4 p-2 rounded " key={tag}  >
                {tag}
              </span>
            ))
          : ''}
      </div>
      <div className="justify-center items-center flex">
        <button className="bg-teal-500 text-white rounded-lg w-60 h-14 font-bold text-lg items-center justify-center  hover:bg-teal-400" onClick={() => setPost(true)}>

          <NavLink to={"\jobApply"}>
            Apply
          </NavLink>
        </button>


      </div>


    </div>
  );
};

export default JobsComponent;
