import { useState, useEffect } from "react";
import { AiOutlineArrowLeft, AiOutlineArrowRight } from "react-icons/ai";
import { sliderData } from "./Slider-data";

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slideLength = sliderData.length;

  const autoScroll = true;
  let slideInterval;
  let intervalTime = 5000;

  const nextSlide = () => {
    setCurrentSlide(currentSlide === slideLength - 1 ? 0 : currentSlide + 1);
    console.log("next");
  };

  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? slideLength - 1 : currentSlide - 1);
    console.log("prev");
  };

  function auto() {
    slideInterval = setInterval(nextSlide, intervalTime);
  }

  useEffect(() => {
    setCurrentSlide(0);
  }, []);

  useEffect(() => {
    if (autoScroll) {
      auto();
    }
    return () => clearInterval(slideInterval);
  }, [currentSlide]);

  return (
    <div className="  high-screen flex items-center  lg:m-20 lg:w-10/12 h-4/12   flex-row bg-teal-100 lg:p-5 rounded-md">
      <AiOutlineArrowLeft className=" text-5xl cursor-pointer" onClick={prevSlide} />

      {sliderData.map((slide, index) => {
        return (
          <div
            className={index === currentSlide ? "slide current" : "slide"}
            key={index}
          >
            {index === currentSlide && (
              <div className=" lg:flex lg:flex-row  w-full  items-center flex-wrap grid grid-cols-2">
                <div>
                <img src={slide.image} alt="slide" className=" lg:h-80 h-56" />
                </div>
                <div className="lg:w-3/12 w-1/12 pl-8   lg:justify-center lg:text-center flex flex-wrap ">
                  <h2 className=" text-lg font-bold">{slide.heading}</h2>
                  <p className=" py-3 font-serif flex ">{slide.desc}</p>
                  <hr className=" bg-slate-600 h-1"/>
                  <button className=" lg:w-40 lg:h-10 lg:px-0 px-10 bg-teal-300 border rounded-2xl mt-3 cursor-pointer">Get Started</button>
                </div>
              </div>
            )}
          </div>
        );
      })}
      <AiOutlineArrowRight className=" justify-end flex items-end text-5xl cursor-pointer" onClick={nextSlide} />
    </div>
  );
};

export default Slider;
